package com.syntax.class02;

public class identifierss {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 1. cannot use keywords as identifiers
		//String new ="Hello"; --> error
		
		//2. cannot have space in identifiers
		//String last name = "Smith";
		
		//3 identifiers cannot start with numbers
		// int 1number = 123;
		
		//4. identifiers cannot contain any special characters except $ or _
		//boolean hello!=true;
		//double $price = 9.44;  --> is allowed
		//float _price1 = 3.44F;  -->is allowed
		
		
		/*
		 * Naming conventions
		 * class should start with Upper case and follow camel casing
		 * variable should start with lower case and follow camel casing
		 */
		
		String Weather = "hot"; // not preferred
		String myWeather = "cold"; // Preferred

	}

}
