public class IfElse7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Prakriti";
		if(name.equals("Puskar")){
			System.out.println("Prakriti loves Puskar");
		}else {
			System.out.println("Puskar loves Prakriti");
		}

	}

}
