package com.syntax.class07;

public class WhileLoopVsIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int time =13;
		
		if(time>12) {
			System.out.println("Good morning");
		}
		while (time>12) {
			System.out.println("Good morning");
			time --;
		}

	}

}
